"use strict";

const path = require("path");

const platform_arch = process.platform + "_" + process.arch;
try {
	module.exports = require(path.join(__dirname, "bin", platform_arch, "loader.node"));
}
catch (error) {
	console.error("Unsupported platform:", platform_arch);
	throw new Error("Missing loader binary. See message above.");
}
